package com.imooc.order;//import org.springframework.cloud.netflix.feign.EnableFeignClients;import org.springframework.beans.factory.annotation.Value;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.cloud.client.SpringCloudApplication;import org.springframework.cloud.client.discovery.EnableDiscoveryClient;import org.springframework.cloud.context.config.annotation.RefreshScope;import org.springframework.cloud.netflix.eureka.EnableEurekaClient;import org.springframework.cloud.openfeign.EnableFeignClients;import org.springframework.context.annotation.ComponentScan;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RestController;@SpringBootApplication@EnableDiscoveryClient@EnableEurekaClient//@EnableCircuitBreaker//扫描商品里的某个包@EnableFeignClients(basePackages = "com.imooc.product.client")//@SpringCloudApplication@ComponentScan(basePackages = "com.imooc")public class OrderApplication {	public static void main(String[] args) {		SpringApplication.run(OrderApplication.class, args);	}}